import React, { useEffect, useState } from "react";
import { Form, Input, Select, Button, Space, Row, Col, DatePicker } from "antd";
import moment from "moment";
import useAxios from "../../hooks/useAxios";

const { Option } = Select;

const TaskForm = ({ initialValues, onSubmit, onCancel, action }) => {
  const userList = useAxios();
  const [form] = Form.useForm();
  const [userLists, setUserList] = useState([]);
  const [formValues, setFormValues] = useState(initialValues);

  const UserList = async () => {
    try {
      const users = await userList.execute({
        url: "users",
        method: "GET",
      });
      console.log("Users:", users);
      setUserList(users);
    } catch (err) {
      console.error("Error fetching users:", err);
    }
  };

  // Log to check if initialValues is resetting
  useEffect(() => {
    UserList();
    console.log("initialValues changed:", initialValues);
    if (initialValues) {
      form.setFieldsValue(initialValues); // Set initial form values
      setFormValues(initialValues); // Store in local state for tracking changes
    }
  }, [initialValues, form]);

  const handleSubmit = (values) => {
    onSubmit(values);
    // Optional: If you want to reset the form after submitting, uncomment the next line
    // form.resetFields();
  };

  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={handleSubmit}
      initialValues={formValues} // Use the local state to persist values
      style={{ borderRadius: "8px", background: "#f9f9f9" }}
    >
      <Row gutter={16}>
        <Col xs={24} md={8}>
          <Form.Item
            name="title"
            label="Task Title"
            rules={[{ required: true, message: "Please enter a task title!" }]}
          >
            <Input placeholder="Enter task title" />
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="description"
            label="Description"
            rules={[
              { required: true, message: "Please enter a task description!" },
            ]}
          >
            <Input.TextArea rows={1} placeholder="Enter task description" />
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="priority"
            label="Priority"
            rules={[{ required: true, message: "Please select a priority!" }]}
          >
            <Select placeholder="Select priority" style={{ width: "100%" }}>
              <Option value="Low">Low</Option>
              <Option value="Medium">Medium</Option>
              <Option value="High">High</Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} md={8}>
          <Form.Item
            name="assignedTo"
            label="Assigned To"
            rules={[{ required: true, message: "Please select an assignee!" }]}
          >
            <Select placeholder="Select assignee" style={{ width: "100%" }}>
              {userLists.map((user) => (
                <Option key={user._id} value={user._id}>
                  {user.name}
                </Option>
              ))}
            </Select>
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="status"
            label="Status"
            rules={[
              { required: true, message: "Please select the task status!" },
            ]}
          >
            <Select placeholder="Select status" style={{ width: "100%" }}>
              <Option value="To Do">To Do</Option>
              <Option value="In Progress">In Progress</Option>
              <Option value="Done">Done</Option>
            </Select>
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="dueDate"
            label="Due Date"
            rules={[{ required: true, message: "Please select a due date!" }]}
          >
            <DatePicker
            value={moment(formValues.dueDate)}
              style={{ width: "100%" }}
              format="YYYY-MM-DD" // This will display date in 2023-12-31 format
              disabledDate={(current) => {
                // Disable all dates before today
                return current && current < moment().startOf("day");
              }}
            />
          </Form.Item>
        </Col>
      </Row>

      <Space style={{ width: "100%", justifyContent: "end" }}>
        <Button onClick={onCancel}>Cancel</Button>
        <Button type="primary" htmlType="submit">
          {initialValues ? "Update Task" : "Add Task"}
        </Button>
      </Space>
    </Form>
  );
};

export default TaskForm;
