import React, { useState } from "react";
import {
  Table,
  Tag,
  Select,
  Button,
  DatePicker,
  Card,
  Space,
  Typography,
  Flex,
  Input,
  Form,
  Tabs,
  Tooltip,
  Modal,
} from "antd";
import { DeleteOutlined } from "@ant-design/icons";
import { FaRegEdit } from "react-icons/fa";
import TaskForm from "../forms/TaskListForm";
import TaskForm1 from "../forms/TaskForm1";
import usePostApi from "../../hooks/usePostApi";

const { Option } = Select;
const { Title } = Typography;
const { TabPane } = Tabs;

const TaskList = () => {
  const { data, loading, error, postData } = usePostApi("tasks/createTask");

  const [tasks, setTasks] = useState([
    {
      id: 1,
      title: "Complete Report",
      description: "Finish the quarterly financial report",
      dueDate: "2024-09-15",
      priority: "High",
      assignedTo: "John Doe",
      status: "To Do",
    },
    {
      id: 2,
      title: "Update Website",
      description: "Revamp the homepage UI",
      dueDate: "2024-09-18",
      priority: "Medium",
      assignedTo: "Jane Smith",
      status: "In Progress",
    },
    {
      id: 3,
      title: "Client Meeting",
      description: "Discuss project requirements",
      dueDate: "2024-09-20",
      priority: "Low",
      assignedTo: "Alice Brown",
      status: "Done",
    },
  ]);

  const [filterStatus, setFilterStatus] = useState("All");
  const [filterPriority, setFilterPriority] = useState("All");
  const [filterDueDate, setFilterDueDate] = useState(null);
  const [currentTask, setCurrentTask] = useState([]);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [activeTabKey, setActiveTabKey] = useState("1");
  const [action, setAction] = useState("entry");

  const TaskEnrey = async (taskData) => {
    const res = await postData({});
  };

  const showModal = (record) => {
    setCurrentTask(record);
    setActiveTabKey("2");
    setAction("update");
  };

  const handleCancel = () => {
    setCurrentTask([]);
    setActiveTabKey("1");
    setAction("entry");
  };

  const handleSubmit = (taskData) => {
    console.log(taskData);

    if (currentTask && currentTask.id) {
      // Update existing task
      setTasks(
        tasks.map((task) =>
          task.id === currentTask.id ? { ...task, ...taskData } : task
        )
      );
    } else {
      // Add new task
      setTasks([...tasks, { ...taskData, id: tasks.length + 1 }]);
    }
    setCurrentTask([]);
    setActiveTabKey("1");
  };

  const updateTask = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
  };

  const deleteTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
  };

  const columns = [
    {
      title: "Title",
      dataIndex: "title",
      key: "title",
      render: (text) => <strong>{text}</strong>,
    },
    {
      title: "Description",
      dataIndex: "description",
      key: "description",
    },
    {
      title: "Due Date",
      dataIndex: "dueDate",
      key: "dueDate",
    },
    {
      title: "Priority",
      dataIndex: "priority",
      key: "priority",
      render: (priority) => (
        <Tag
          color={
            priority === "High"
              ? "red"
              : priority === "Medium"
              ? "orange"
              : "green"
          }
        >
          {priority}
        </Tag>
      ),
    },
    {
      title: "Assigned To",
      dataIndex: "assignedTo",
      key: "assignedTo",
    },
    {
      title: "Status",
      dataIndex: "status",
      key: "status",
      render: (status, record) => (
        <Select
          defaultValue={status}
          style={{ width: 120 }}
          onChange={(value) => updateTask({ ...record, status: value })}
        >
          <Option value="To Do">To Do</Option>
          <Option value="In Progress">In Progress</Option>
          <Option value="Done">Done</Option>
        </Select>
      ),
    },
    {
      title: "Action",
      key: "action",
      render: (_, record) => (
        <Space>
          <Tooltip title="Edit Task">
            <Button onClick={() => showModal(record)}>
              <FaRegEdit size={15} />
            </Button>
          </Tooltip>

          <Tooltip title="Delete Team">
            <Button
              type="danger"
              size="small"
              onClick={() => deleteTask(record.id)}
            >
              <DeleteOutlined size={15} />
            </Button>
          </Tooltip>
        </Space>
      ),
    },
  ];

  const TabChange = (key) => {
    setAction("entry");
    console.log(key);
    if (key == "1") {

      setCurrentTask([]);
    }
  };

  const filteredTasks = tasks.filter((task) => {
    return (
      (filterStatus === "All" || task.status === filterStatus) &&
      (filterPriority === "All" || task.priority === filterPriority) &&
      (!filterDueDate || task.dueDate === filterDueDate.format("YYYY-MM-DD"))
    );
  });

  return (
    <Card style={{ margin: "20px auto", borderRadius: "10px" }}>
      <Title level={5} style={{ textAlign: "start", marginBottom: 0 }}>
        Task Management
      </Title>

      <Tabs
        activeKey={activeTabKey}
        onChange={(key) => {
          setActiveTabKey(key); 
          TabChange(key); 
        }}
        centered
      >
        <TabPane tab="Task Details" key="1">
          {/* Filters */}
          <Card
            style={{
              marginBottom: 0,
              borderRadius: "8px",
              background: "#f9f9f9",
              padding: "0px",
            }}
          >
            <Space gap="small" wrap="wrap" justify="center">
              <Select
                defaultValue="All"
                style={{ width: 150 }}
                onChange={setFilterStatus}
                placeholder="Filter by Status"
              >
                <Option value="All">All Status</Option>
                <Option value="To Do">To Do</Option>
                <Option value="In Progress">In Progress</Option>
                <Option value="Done">Done</Option>
              </Select>

              <Select
                defaultValue="All"
                style={{ width: 150 }}
                onChange={setFilterPriority}
                placeholder="Filter by Priority"
              >
                <Option value="All">All Priority</Option>
                <Option value="Low">Low</Option>
                <Option value="Medium">Medium</Option>
                <Option value="High">High</Option>
              </Select>

              <DatePicker
                style={{ width: 150 }}
                onChange={setFilterDueDate}
                placeholder="Filter by Due Date"
              />
            </Space>
          </Card>

          {/* Task Table */}
          <Table
            dataSource={filteredTasks}
            columns={columns}
            rowKey="id"
            bordered
            pagination={{ pageSize: 5 }}
            style={{ borderRadius: "8px", overflow: "hidden" }}
          />
        </TabPane>

        <TabPane tab="Add Task" key="2">
          {action == "update" ? (
            <TaskForm
              action={action}
              initialValues={currentTask}
              onSubmit={handleSubmit}
              onCancel={handleCancel}
            />
          ) : (
            <TaskForm1 />
          )}
        </TabPane>
      </Tabs>
    </Card>
  );
};

export default TaskList;
