import React, { useEffect, useState } from "react";
import { Form, Input, Select, DatePicker, Button, Space, Row, Col } from "antd";
import usePostApi from "../../hooks/usePostApi";

const { Option } = Select;

const TaskForm = ({ initialValues, onSubmit, onCancel, action }) => {
  const { data, loading, error, postData } = usePostApi('tasks/createTask');
  const [form] = Form.useForm();


  useEffect(() => {
    if (initialValues) {
      form.setFieldsValue(initialValues);
    }
  }, [initialValues]);

  const handleSubmit = (values) => {
    onSubmit(values);
    form.resetFields();
  };

  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={handleSubmit}
      initialValues={initialValues}
      style={{ borderRadius: "8px", background: "#f9f9f9" }}
    >
      <Row gutter={16}>
        <Col xs={24} md={8}>
          <Form.Item
            name="title"
            label="Task Title"
            rules={[{ required: true, message: "Please enter a task title!" }]}
          >
            <Input placeholder="Enter task title" />
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="description"
            label="Description"
            rules={[
              { required: true, message: "Please enter a task description!" },
            ]}
          >
            <Input.TextArea rows={1} placeholder="Enter task description" />
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="priority"
            label="Priority"
            rules={[{ required: true, message: "Please select a priority!" }]}
          >
            <Select placeholder="Select priority" style={{ width: "100%" }}>
              <Option value="Low">Low</Option>
              <Option value="Medium">Medium</Option>
              <Option value="High">High</Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} md={8}>
          <Form.Item
            name="assignedTo"
            label="Assigned To"
            rules={[{ required: true, message: "Please select an assignee!" }]}
          >
            <Select placeholder="Select assignee" style={{ width: "100%" }}>
              <Option value="John Doe">John Doe</Option>
              <Option value="Jane Smith">Jane Smith</Option>
              <Option value="Alice Brown">Alice Brown</Option>
            </Select>
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="status"
            label="Status"
            rules={[
              { required: true, message: "Please select the task status!" },
            ]}
          >
            <Select placeholder="Select status" style={{ width: "100%" }}>
              <Option value="To Do">To Do</Option>
              <Option value="In Progress">In Progress</Option>
              <Option value="Done">Done</Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>

      {/* <Form.Item
        name="dueDate"
        label="Due Date"
        rules={[{ required: true, message: "Please select a due date!" }]}
      >
        <DatePicker placeholder="Select due date" style={{ width: "100%" }} />
      </Form.Item> */}

      <Space style={{ width: "100%", justifyContent: "end" }}>
        <Button onClick={onCancel}>Cancel</Button>
        <Button type="primary" htmlType="submit">
          {action == "update" ? "Update Task" : "Add Task"}
        </Button>
      </Space>
    </Form>
  );
};

export default TaskForm;
