import React, { useEffect, useState } from "react";
import { Form, Input, Select, Button, Space, Row, Col } from "antd";
import useAxios from "../../hooks/useAxios";

const { Option } = Select;

const TaskForm = ({ initialValues, onSubmit, onCancel, action }) => {
  const userRequest = useAxios();
  const [form] = Form.useForm();
  const [userLists, setUserLists] = useState([]);
  const [localInitialValues, setLocalInitialValues] = useState(initialValues);

  // Function to fetch users
  const fetchUsers = async () => {
    try {
      const users = await userRequest.execute({
        url: "users",
        method: "GET",
      });
      console.log("Users:", users);
      setUserLists(users);
    } catch (err) {
      console.error("Error fetching users:", err);
    }
  };

  // When the component mounts, fetch the user list.
  useEffect(() => {
    fetchUsers();
  }, []);

  // When initialValues or userLists change, update the form fields.
  // If initialValues.assignedTo is just an ID, we convert it to an object
  // { value: userId, label: userName } using the fetched userLists.
  useEffect(() => {
    if (initialValues && userLists.length > 0) {
      let updatedValues = { ...initialValues };

      // Check if assignedTo is a string (or ID) rather than an object.
      if (
        updatedValues.assignedTo &&
        typeof updatedValues.assignedTo === "string"
      ) {
        const assignedUser = userLists.find(
          (user) => user._id === updatedValues.assignedTo
        );
        if (assignedUser) {
          updatedValues.assignedTo = {
            value: assignedUser._id,
            label: assignedUser.name || assignedUser._id,
          };
        }
      }
      // Update local state and form fields.
      setLocalInitialValues(updatedValues);
      form.setFieldsValue(updatedValues);
    }
  }, [initialValues, userLists, form]);

  // Handle form submission.
  const handleSubmit = (values) => {
    // Since we use labelInValue, the assignedTo field is an object.
    // Convert it back to just the user ID.
    if (values.assignedTo && values.assignedTo.value) {
      values.assignedTo = values.assignedTo.value;
    }
    onSubmit(values);
  };

  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={handleSubmit}
      // Use the local state to persist values
      initialValues={localInitialValues}
      style={{ borderRadius: "8px", background: "#f9f9f9", padding: 16 }}
    >
      <Row gutter={16}>
        <Col xs={24} md={8}>
          <Form.Item
            name="title"
            label="Task Title"
            rules={[{ required: true, message: "Please enter a task title!" }]}
          >
            <Input placeholder="Enter task title" />
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="description"
            label="Description"
            rules={[
              { required: true, message: "Please enter a task description!" },
            ]}
          >
            <Input.TextArea rows={1} placeholder="Enter task description" />
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="priority"
            label="Priority"
            rules={[{ required: true, message: "Please select a priority!" }]}
          >
            <Select placeholder="Select priority" style={{ width: "100%" }}>
              <Option value="Low">Low</Option>
              <Option value="Medium">Medium</Option>
              <Option value="High">High</Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} md={8}>
          <Form.Item
            name="assignedTo"
            label="Assigned To"
            rules={[{ required: true, message: "Please select an assignee!" }]}
          >
            {/* 
              Using labelInValue makes the value an object {value, label}.
              When the user selects an option, both will be available.
            */}
            <Select
              placeholder="Select assignee"
              style={{ width: "100%" }}
              labelInValue
            >
              {userLists.map((user) => (
                <Option key={user._id} value={user._id}>
                  {user.name || user._id}
                </Option>
              ))}
            </Select>
          </Form.Item>
        </Col>

        <Col xs={24} md={8}>
          <Form.Item
            name="status"
            label="Status"
            rules={[
              { required: true, message: "Please select the task status!" },
            ]}
          >
            <Select placeholder="Select status" style={{ width: "100%" }}>
              <Option value="To Do">To Do</Option>
              <Option value="In Progress">In Progress</Option>
              <Option value="Done">Done</Option>
            </Select>
          </Form.Item>
        </Col>
      </Row>

      <Space style={{ width: "100%", justifyContent: "end" }}>
        <Button onClick={onCancel}>Cancel</Button>
        <Button type="primary" htmlType="submit">
          {initialValues ? "Update Task" : "Add Task"}
        </Button>
      </Space>
    </Form>
  );
};

export default TaskForm;
