import React, { useEffect, useState } from "react";
import { Modal, Form, Input, Upload, Button } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import usePostApi from "../../hooks/usePostApi";

const UpdateUserModal = ({ visible, onClose, data }) => {
// const { data, error, postData } = usePostApi("users/updateuser");
  const [form] = Form.useForm();
  const [file, setFile] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (data) {
      form.setFieldsValue({
        name: data.name,
        email: data.email,
        profileImage: data.profileImage,
      });
    }
  }, [data, form]);

  const handleFileChange = (info) => {
    const file = info.file.originFileObj || info.file;
    if (!file) return;
    setFile(file);
    setPreviewImage(URL.createObjectURL(file));
  };

  const handleUpdateUser = async (values) => {
    try {
      setLoading(true);
      const formData = new FormData();
      formData.append("name", values.name);
      formData.append("email", values.email);
      if (file) formData.append("profileImage", file);

      const response = await axios.put("http://localhost:5000/api/users/updateuser", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      console.log("User updated:", response.data);
      setLoading(false);
      onClose();
    } catch (error) {
      console.error("Error updating user:", error);
      setLoading(false);
    }
  };

  return (
    <Modal
      title="Update User"
      visible={visible}
      onCancel={onClose}
      footer={null}
      maskClosable={false} 
    >
      <Form form={form} onFinish={handleUpdateUser} layout="vertical">
        <Form.Item label="Name" name="name" rules={[{ required: true }]}>
          <Input placeholder="Enter your name" />
        </Form.Item>

        <Form.Item label="Email" name="email" rules={[{ required: true }]}>
          <Input placeholder="Enter your email" />
        </Form.Item>

        <Form.Item label="Profile Image">
          <Upload
            listType="picture-card"
            maxCount={1}
            showUploadList={false}
            beforeUpload={() => false} // Prevent automatic upload
            onChange={handleFileChange}
          >
            {previewImage ? (
              <img src={previewImage} alt="Preview" style={{ width: "70%" }} />
            ) : (
              <Button icon={<UploadOutlined />}>Upload Image</Button>
            )}
          </Upload>
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" loading={loading}>
            Update
          </Button>
        </Form.Item>
      </Form>
    </Modal>
  );
};

export default UpdateUserModal;
